CREATE TABLE `obra` (
  `titulo` varchar(100) NOT NULL,
  `fecha` varchar(50) DEFAULT NULL,
  `tecnica` varchar(100) DEFAULT NULL,
  `dimension` varchar(50) DEFAULT NULL,
