-- Estructura para la vista `vistafavs`
--
DROP TABLE IF EXISTS `vistafavs`;

CREATE ALGORITHM=UNDEFINED DEFINER=`servidores`@`localhost` SQL SECURITY DEFINER VIEW `vistafavs`  AS SELECT uuid() AS `id`, `f`.`cod_obra_fav` AS `cod_obra_fav`, `f`.`email_usu` AS `email`, `f`.`cod_obra` AS `cod_obra`, `o`.`titulo` AS `titulo_obra`, `a`.`nombre` AS `autor`, `o`.`fecha` AS `fecha_obra`, `o`.`imagen` AS `imagen_obra` FROM ((`obra` `o` join `obrafav` `f` on(`o`.`cod_obra` = `f`.`cod_obra`)) join `artista` `a` on(`o`.`cod_artista` = `a`.`cod_artista`)) ;

-- --------------------------------------------------------

--
-- Estructura para la vista `vistagaleria`
--
DROP TABLE IF EXISTS `vistagaleria`;

CREATE ALGORITHM=UNDEFINED DEFINER=`servidores`@`localhost` SQL SECURITY DEFINER VIEW `vistagaleria`  AS SELECT uuid() AS `id`, `g`.`cod_galeria` AS `cod_galeria`, `g`.`email_usu` AS `email`, `g`.`nombre` AS `nombre_galeria`, `g`.`descripcion` AS `descripcion_galeria`, `x`.`cod_obra` AS `cod_obra`, `o`.`titulo` AS `titulo`, `o`.`fecha` AS `fecha_obra`, `a`.`nombre` AS `autor`, `o`.`imagen` AS `imagen` FROM (((`galeria` `g` join `obragaleria` `x` on(`g`.`cod_galeria` = `x`.`cod_galeria`)) join `obra` `o` on(`o`.`cod_obra` = `x`.`cod_obra`)) join `artista` `a` on(`a`.`cod_artista` = `o`.`cod_artista`)) ;

-- --------------------------------------------------------

--
-- Estructura para la vista `vistaobra`
--
DROP TABLE IF EXISTS `vistaobra`;

CREATE ALGORITHM=UNDEFINED DEFINER=`servidores`@`localhost` SQL SECURITY DEFINER VIEW `vistaobra`  AS SELECT uuid() AS `id`, `o`.`cod_obra` AS `cod_obra`, `o`.`titulo` AS `titulo_obra`, `a`.`nombre` AS `autor_obra`, `a`.`cod_artista` AS `cod_artista`, `c`.`nombre` AS `corriente_obra`, `c`.`cod_corriente` AS `cod_corriente`, `o`.`fecha` AS `fecha_obra`, `o`.`tecnica` AS `tecnica_obra`, `o`.`dimension` AS `dimension_obra`, `o`.`ubicacion` AS `ubicacion_obra`, `o`.`descripcion` AS `descripcion_obra`, `o`.`imagen` AS `imagen_obra` FROM ((`obra` `o` join `artista` `a` on(`o`.`cod_artista` = `a`.`cod_artista`)) join `corriente` `c` on(`o`.`cod_corriente` = `c`.`cod_corriente`)) ;

